@import url("https://fonts.googleapis.com/css2?family=Roboto:wght@100;300;400;500;700&display=swap");

:root {
  --blue-brazil: #002277;
  --yellow-brazil: #e2cb00;
  --green-brazil: #03852d;
  --background-color: #e4e3d9;
}

/* All */
* {
  box-sizing: border-box;
  font-family: "Roboto", sans-serif;
  font-size: 12px;
  margin: 0;
}

body {
  display: flex;
  flex-direction: column;
  width: 100vw;
  background-color: var(--background-color);
}

h2 {
  font-size: 3rem;
  text-transform: uppercase;
  font-weight: lighter;
  border-bottom: 1px solid black;
  margin-top: 2%;
  padding-bottom: 0.5%;
}

h3 {
  font-size: 2rem;
  text-transform: uppercase;
  font-weight: lighter;
  border-bottom: 1px solid black;
  margin-top: 2%;
  padding-bottom: 0.5%;
}

/* Header */

nav {
  width: 80%;
  margin-left: 0;
}

body:not(#portada) nav {
  margin: 0 auto;
}

header {
  padding: 10px;
}

nav ul {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  padding: 0;
}

nav ul li {
  list-style: none;
}

nav ul li a {
  text-decoration: none;
  color: var(--blue-brazil);
}

.current_page {
  color: var(--green-brazil);
}

/* Portada */
#portada nav {
  width: 70%;
}
h1 {
  text-align: right;
  padding-left: 25%;
  padding-right: 50%;
  margin-bottom: 50%;
  font-weight: normal;
  align-self: center;
  font-size: 7rem;
  color: var(--blue-brazil);
}
h1 span {
  font-size: 7rem;
  color: var(--yellow-brazil);
}

#portada {
  background-image: url("./img/Movil.png");
  background-repeat: no-repeat;
  background-size: 100% 100%;
  height: 100vh;
}

#portada main {
  width: 30%;
  height: 100vh;
  margin: 0 auto;
  display: flex;
  justify-content: space-around;
}

#portada main img {
  display: none;
}

/* Categoria*/

#categoria h2 {
  text-align: right;
}

#categoria main {
  width: 95%;
  display: flex;
  flex-direction: column;
  margin: 0 auto;
}

#categoria main section {
  width: 90%;
  margin: 0 auto;
  margin-top: 4%;
}

#categoria main section ul {
  list-style: none;
  padding: 0;
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-around;
}

#categoria main section ul li {
  width: 200px;
  /*border: 1px solid red;*/
  margin-top: 2%;
}
#categoria main section ul li img {
  width: 200px;
  height: 200px;
  border-radius: 100%;
  margin: 0 auto;
  /*border: 1px solid red;*/
}

#categoria main section ul li a {
  display: flex;
  flex-direction: column;
  text-decoration: none;
}
.name_categoria {
  margin: 0 auto;
  margin-top: 5%;
  margin-bottom: 10%;
  color: var(--blue-brazil);
}
.name_categoria div {
  font-size: 1.2rem;
}

/* Detalle */

#mandioca main {
  width: 95%;
  margin: 0 auto;
  text-align: justify;
}

#mandioca main p {
  width: 95%;
  margin: 0 auto;
  margin-top: 6%;
}
#mandioca main h3 {
  margin-bottom: 5%;
  margin-top: 11%;
}

#mandioca main h2 {
  text-align: right;
}

#mandioca main li {
  margin-bottom: 5%;
  background-size: 100px;
}

#mandioca iframe {
  width: 100%;
  margin-bottom: 7%;
}

#mandioca section:last-child img {
  width: 100px;
  height: 100px;
  border-radius: 100%;
  align-self: center;
}
#mandioca section:last-child div div {
  display: flex;
  float: right;
  flex-direction: column;
  text-align: center;
  text-indent: 0;
}

#mandioca section:last-child div div p {
  text-indent: 0;
}

#mandioca section:last-child div div p:first-child {
  font-weight: bold;
  text-transform: uppercase;
}

/* Presentacion */

#presentacion img {
  width: 50%;
}

#presentacion main h2 {
  text-align: right;
  margin-bottom: 5%;
}

#presentacion main {
  width: 95%;
  margin: 0 auto;
  text-align: justify;
}
#presentacion main p {
  padding-bottom: 3%;
}
#presentacion div p:first-child {
  text-align: center;
  padding-bottom: 4%;
}
#presentacion main img {
  display: block;
  margin: auto;
  padding-bottom: 6%;
}

/* Enlaces */

#enlaces main h2 {
  text-align: right;
  margin-bottom: 5%;
}
#enlaces main {
  width: 95%;
  margin: 0 auto;
  text-align: justify;
}

#enlaces main li {
  padding-bottom: 1%;
}

/*            *
 *   Tablet   *
 *            */

@media screen and (min-width: 600px) and (max-width: 1025px) {
  * {
    font-size: 16px;
  }
  /*Header*/

  nav ul li a {
    font-size: 1rem;
  }
  /* Portada */

  h1 {
    padding-left: 25%;
    padding-right: 30%;
    padding-bottom: 10%;
  }

  #portada {
    background-image: url("./img/Tablet.png");
  }
  #portada main {
    width: 40%;
  }

  /*  Detalle */

  #mandioca main p {
    margin-top: 2%;
  }

  #mandioca main h3 {
    margin-bottom: 2%;
    margin-top: 7%;
  }
}

/*                 *
 *   Escritorio    *
 *                 */

@media screen and (min-width: 1025px) {
  * {
    font-size: 16px;
  }

  body {
    display: flex;
    flex-direction: column;
    width: 100vw;
    background-color: var(--background-color);
  }

  /* Header */

  nav {
    width: 29%;
    margin: 0 auto;
  }

  #portada nav {
    width: 29%;
  }

  nav ul li a:hover {
    font-weight: bold;
  }

  /* Portada */

  h1 {
    font-size: 6rem;
    text-shadow: -1px 0 var(--yellow-brazil), 0 1px var(--yellow-brazil),
      1px 0 var(--yellow-brazil), 0 -1px var(--yellow-brazil);
    flex: 2;

    padding-left: 0;
    padding-right: 7%;
    margin-bottom: 4%;
    font-weight: normal;
    align-self: center;
  }

  h1 span {
    font-size: 6rem;
  }

  #portada {
    background-image: url("./img/escritorio.png");
  }

  #portada main {
    width: 80%;
  }

  #portada main img {
    width: 30%;
    height: 100vh;
    margin-right: 10%;
    display: initial;
  }

  /* Categoría*/

  #categoria main section ul li {
    width: 400px;
  }
  #categoria main section ul li img {
    width: 300px;
    height: 300px;
    border-radius: 100%;
    display: block;
  }
  #categoria main section ul li a {
    position: relative;
    left: 12%;
  }

  #categoria main section ul li a:hover .name_categoria {
    opacity: 1;
  }
  .name_categoria {
    position: absolute;
    right: 1px;
    left: 1px;
    top: 0;
    bottom: 0;
    height: 260px;
    width: 260px;
    opacity: 0;
    transition: 0.5s ease;
    background-color: var(--yellow-brazil);
    border-radius: 100%;
  }

  .name_categoria div {
    color: var(--blue-brazil);
    font-size: 1rem;
    position: absolute;
    top: 50%;
    left: 50%;
    -webkit-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    text-align: center;
  }

  /*  Detalle */

  #mandioca main p {
    margin-top: 2%;
  }

  #mandioca main h3 {
    margin-bottom: 2%;
    margin-top: 4%;
  }
  #mandioca main li {
    margin-bottom: 2%;
  }
  #mandioca iframe {
    height: 800px;
  }
  #mandioca main h2 {
    font-size: 3rem;
    margin-top: 2%;
    text-align: right;
  }
  #mandioca main div section:first-child p {
    width: 80%;
  }

  #mandioca main div section:nth-child(2n) div {
    width: 80%;
    margin: 0 auto;
  }

  /* Presentacion */

  #presentacion img {
    width: 40%;
  }

  #presentacion main h2 {
    margin-top: 2%;
    margin-bottom: 5%;
  }

  #presentacion main {
    width: 95%;
  }
  #presentacion main p {
    padding-bottom: 3%;
  }
  #presentacion main div {
    width: 80%;
    margin: 0 auto;
  }
  #presentacion div p:first-child {
    text-align: center;
    padding-bottom: 4%;
  }
  #presentacion main img {
    display: block;
    margin: auto;
    padding-bottom: 6%;
  }

  /* Enlaces */
  #enlaces main h2 {
    text-align: right;
    margin-bottom: 5%;
  }
  #enlaces main div {
    width: 80%;
    margin: 0 auto;
  }
}
